export function LOGCAT(str)
{
if(print_log) console.log('[' + getCurrentTime() + ']  ' + str);
}

export function getCurrentTime()
{ 
var now = new Date();      
var year = now.getFullYear();      
var month = now.getMonth() + 1;    
var day = now.getDate();           
var hh = now.getHours();          
var mm = now.getMinutes();          

var clock = year + "-";      
if(month < 10)
clock += "0";
clock += month + "-";       
if(day < 10)
clock += "0";           
clock += day + " ";   
if(hh < 10)
clock += "0";       
clock += hh + ":";
if (mm < 10) 
clock += '0'; 
clock += mm; 
return(clock); 
}

